import React from "react";
import "./styles.css";

function InputDocument({
  firstContent,
  firstFileInputRef,
  setFirstContent,
  setFirstFileName,
  setActiveInput
}) {

  const handleResize = (e) => {
    e.target.style.height = "auto";
    e.target.style.height = `${e.target.scrollHeight}px`;
  };

  const handleFileUpload = (e, order) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setFirstFileName(file.name);
        setFirstContent(event.target.result);
      };
      reader.readAsText(file);
    } else {
      alert("Please upload a valid file");
    }
  };

  return (
    <div className="container">
      <div className="input-group">
        <div className="input-container">
          <label className="input-text">Enter First Content</label>
          <input
            type="file"
            onChange={(e) => handleFileUpload(e, "1")}
            onFocus={() => setActiveInput("1")}
            className="input-text"
            ref={firstFileInputRef}
          />
          <textarea
            id="first-content"
            value={firstContent}
            onChange={(e) => {
              setFirstContent(e.target.value);
              handleResize(e);
            }}
            placeholder="Enter first content"
            onFocus={() => setActiveInput("1")}
          />
        </div>
      </div>
    </div>
  );
}

export default InputDocument;
