import React, { useState, useRef} from "react";
import { useNavigate } from "react-router-dom";
import logo from "./constants/edicom-logo.png";
import InputDocument from "./components/InputDocuments.js";
import DocumentsButtons from "./components/DocumentsButtons";

const DocumentsPage = () => {
    const [firstContent, setFirstContent] = useState("");
    const [fisrtFileName, setFirstFileName] = useState(""); 
    const [output, setOutput] = useState("");
    const firstFileInputRef = useRef(null);
    const [activeInput, setActiveInput] = useState("1");
    const [firstComparedOutput, setFirstComparedOutput] = useState("");
    const navigate = useNavigate();
    const [fileNames, setFileNames] = useState([]);

    const handleButtonClick = () => {
        navigate("/"); 
      };

    const handleClear = () => {
      setFirstContent("");
      setFileNames([])
      setFirstFileName("");
      setOutput("");
      setFirstComparedOutput("");
      if (firstFileInputRef.current) {
        firstFileInputRef.current.value = "";
      }
    };

    const handleConvert = async () => {
      if (fileNames.length !== 1) {
        alert("Please upload only one file for conversion.");
        return;
      }

      const file = document.querySelector("input[type='file']").files[0];
      const fileType = file.type;

      if (fileType !== "application/pdf" && fileType !== "application/vnd.openxmlformats-officedocument.wordprocessingml.document") {
        alert("Please upload a PDF or DOCX file.");
        return;
      }

      const formData = new FormData();
      formData.append("file", file);

      try {
        const response = await axios.post("/api/files/convert", formData, {
          headers: { "Content-Type": "multipart/form-data" },
          responseType: "blob", // Ensure we get back a file
        });

        // Create a download link for the converted file
        const url = window.URL.createObjectURL(new Blob([response.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", `converted_file.${fileType === "application/pdf" ? "docx" : "pdf"}`);
        document.body.appendChild(link);
        link.click();
        link.remove();

      } catch (error) {
        alert("Conversion failed:", error);
      }
    };

    const handleGlobal = () => {
      setFirstContent("");
      setFirstFileName("");
      setOutput("");
      setFirstComparedOutput("");
      if (firstFileInputRef.current) {
        firstFileInputRef.current.value = "";
      }
    };

    return (
    <div className="App">
        <header className="header">
        <div className="logo-container">
            <img src={logo} alt="Logo" className="logo" />
        </div>
        <div className="title-container">
            <h1 className="title">Documents Handler (PDF - DOCX)</h1>
            <button className="new-page-button" onClick={handleButtonClick}>JSON - XML - EDI - PDF Handler</button>
        </div>
        </header>
        <InputDocument
        fileNames={fileNames}
        setFileNames={setFileNames}
        setFirstContent={setFirstContent} 
        setFirstFileName={setFirstFileName} 
        setActiveInput={setActiveInput}
        />
        <DocumentsButtons 
        handleClear={handleClear} 
        handleConvert={handleConvert}  
        handleGlobal={handleGlobal} 
      />
    </div>
    );
    };

export default DocumentsPage;
