import React, { createContext, useState, useRef } from 'react';

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [firstContent, setFirstContent] = useState("");
  const [secondContent, setSecondContent] = useState("");
  const [firstFileName, setFirstFileName] = useState(""); 
  const [secondFileName, setSecondFileName] = useState("");
  const [output, setOutput] = useState("");
  const firstFileInputRef = useRef(null);
  const secondFileInputRef = useRef(null);
  const [action, setAction] = useState("encode");
  const [activeInput, setActiveInput] = useState("1");
  const [showDiv, setShowDiv] = useState(false);
  const [firstComparedOutput, setFirstComparedOutput] = useState("");
  const [secondComparedOutput, setSecondComparedOutput] = useState("");

  return (
    <AppContext.Provider value={{
      firstContent, setFirstContent,
      secondContent, setSecondContent,
      firstFileName, setFirstFileName,
      secondFileName, setSecondFileName,
      output, setOutput,
      firstFileInputRef,
      secondFileInputRef,
      action, setAction,
      activeInput, setActiveInput,
      showDiv, setShowDiv,
      firstComparedOutput, setFirstComparedOutput,
      secondComparedOutput, setSecondComparedOutput
    }}>
      {children}
    </AppContext.Provider>
  );
};
