import React from "react";
import "./styles.css";

const DocumentsButtons = ({ handleConvert, handleClear, handleMerge, handleSplitPdf, splitFirstPage, setSplitFirstPage, splitLastPage, setSplitLastPage}) => {
  return (
    <div className="button-group">
      <button className="compare-button" onClick={handleConvert}>
        Convert
      </button>
      <button className="convert-button" onClick={handleMerge}>
        Merge
      </button>
      <button className="convert-button" onClick={handleSplitPdf}>
        Split
      </button>
      <button className="clear-button" onClick={handleClear}>
        Clear
      </button>
      <div>
        <label>First Page:</label>
        <input
          type="number"
          value={splitFirstPage}
          onChange={(e) => setSplitFirstPage(Number(e.target.value))}
          min="1"
        />
      </div>

      <div>
        <label>Last Page:</label>
        <input
          type="number"
          value={splitLastPage}
          onChange={(e) => setSplitLastPage(Number(e.target.value))}
          min="1"
        />
      </div>

    </div>
  );
};

export default DocumentsButtons;
