import React, { useState, useRef} from "react";
import { useNavigate } from "react-router-dom";
import logo from "./constants/edicom-logo.png";
import InputDocument from "./components/InputDocuments.js";
import DocumentsButtons from "./components/DocumentsButtons";
import OutputDocument from "./components/OutputDocument.js"
const DocumentsPage = () => {
    const [firstContent, setFirstContent] = useState("");
    const [uploadedFileName, setUploadedFileName] = useState(""); 
    const [output, setOutput] = useState("");
    const fileInputRef = useRef(null);
    const [activeInput, setActiveInput] = useState("1");
    const [firstComparedOutput, setFirstComparedOutput] = useState("");
    const navigate = useNavigate();
    const [fileNames, setFileNames] = useState([]);

    const handleButtonClick = () => {
        navigate("/"); 
      };

    const handleClear = () => {
      setFirstContent("");
      setFileNames([])
      setUploadedFileName("");
      setOutput("");
      setFirstComparedOutput("");
    };

    const handleConvert = async () => {
      if (fileNames.length !== 1) {
        alert("Please upload only one file for conversion.");
        return;
      }

      const file = document.querySelector("input[type='file']").files[0];
      const fileType = file.type;

      if (
        fileType !== "application/pdf" &&
        fileType !== "application/vnd.openxmlformats-officedocument.wordprocessingml.document"
      ) {
        alert("Please upload a PDF or DOCX file.");
        return;
      }

      const formData = new FormData();
      formData.append("file", file);

      try {
        const response = await fetch("http://localhost:8080/diffFormatter/convertFile", {
          method: "POST",
          body: formData,
        });

        if (!response.ok) {
          throw new Error("Conversion failed");
        }

        const blob = await response.blob(); // Get the response as a blob for download
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement("a");

        link.href = url;
        link.setAttribute(
          "download",
          `converted_file.${fileType === "application/pdf" ? "docx" : "pdf"}`
        ); // Set filename and extension based on conversion type
        document.body.appendChild(link);
        link.click();
        link.remove();

      } catch (error) {
        alert("Conversion failed:", error);
      }
    };    

    const handleGlobal = () => {
      setFirstContent("");
      setUploadedFileName("");
      setOutput("");
      setFirstComparedOutput("");
      if (firstFileInputRef.current) {
        firstFileInputRef.current.value = "";
      }
    };

    return (
    <div className="App">
        <header className="header">
        <div className="logo-container">
            <img src={logo} alt="Logo" className="logo" />
        </div>
        <div className="title-container">
            <h1 className="title">Documents Handler (PDF - DOCX)</h1>
            <button className="new-page-button" onClick={handleButtonClick}>JSON - XML - EDI - PDF Handler</button>
        </div>
        </header>
        <InputDocument
        fileNames={fileNames}
        setFileNames={setFileNames}
        fileInputRef={fileInputRef} 
        setUploadedFileName={setUploadedFileName} 
        setActiveInput={setActiveInput}
        />
        <DocumentsButtons 
        handleClear={handleClear} 
        handleConvert={handleConvert}  
        handleGlobal={handleGlobal} 
        />
        <OutputDocument/>
    </div>
    );
    };

export default DocumentsPage;
