import React, { useState, useRef} from "react";
import { useNavigate } from "react-router-dom";
import logo from "./constants/edicom-logo.png";
import InputDocument from "./components/InputDocuments.js";
import Button from "./components/Button";

const DocumentsPage = () => {
    const [firstContent, setFirstContent] = useState("");
    const [fisrtFileName, setFirstFileName] = useState(""); 
    const [output, setOutput] = useState("");
    const firstFileInputRef = useRef(null);
    const [activeInput, setActiveInput] = useState("1");
    const [firstComparedOutput, setFirstComparedOutput] = useState("");
    const navigate = useNavigate();

    const handleButtonClick = () => {
        navigate("/"); 
      };

      const handleClear = () => {
        setFirstContent("");
        setFirstFileName("");
        setOutput("");
        setFirstComparedOutput("");
        if (firstFileInputRef.current) {
          firstFileInputRef.current.value = "";
        }
      };

      const handleCompare = () => {
        setFirstContent("");
        setFirstFileName("");
        setOutput("");
        setFirstComparedOutput("");
        if (firstFileInputRef.current) {
          firstFileInputRef.current.value = "";
        }
      };

      const handleGlobal = () => {
        setFirstContent("");
        setFirstFileName("");
        setOutput("");
        setFirstComparedOutput("");
        if (firstFileInputRef.current) {
          firstFileInputRef.current.value = "";
        }
      };

    return (
    <div className="App">
        <header className="header">
        <div className="logo-container">
            <img src={logo} alt="Logo" className="logo" />
        </div>
        <div className="title-container">
            <h1 className="title">Documents Handler (PDF - DOCX)</h1>
            <button onClick={handleButtonClick}>Go to New Page</button>
        </div>
        </header>
        <InputDocument
        firstContent={firstContent}
        firstFileInputRef={firstFileInputRef}
        setFirstContent={setFirstContent} 
        setFirstFileName={setFirstFileName} 
        setActiveInput={setActiveInput}
        />
        <Button 
        handleClear={handleClear} 
        handleCompare={handleCompare}  
        handleGlobal={handleGlobal} 
      />
    </div>
    );
    };

export default DocumentsPage;
